import React, { useState } from 'react';
import MovieRecommender from '../components/MovieRecommender/MovieRecommender.js'

function SearchMovie() {
    const moviesTest = [
      {
        id: 1,
        title: 'Movie Title 1',
        description: 'This is a brief description of Movie 1.',
        posterUrl: 'https://via.placeholder.com/150'
      },
      {
        id: 2,
        title: 'Movie Title 2',
        description: 'This is a brief description of Movie 2.',
        posterUrl: 'https://via.placeholder.com/150'
      },
      {
        id: 3,
        title: 'Movie Title 3',
        description: 'This is a brief description of Movie 3.',
        posterUrl: 'https://via.placeholder.com/150'
      },
      {
        id: 4,
        title: 'Movie Title 4',
        description: 'This is a brief description of Movie 4.',
        posterUrl: 'https://via.placeholder.com/150'
      },
      {
        id: 5,
        title: 'Movie Title 5',
        description: 'This is a brief description of Movie 5.',
        posterUrl: 'https://via.placeholder.com/150'
      },
      {
        id: 6,
        title: 'Movie Title 6',
        description: 'This is a brief description of Movie 6.',
        posterUrl: 'https://via.placeholder.com/150'
      },
      {
          id: 6,
          title: 'Movie Title 6',
          description: 'This is a brief description of Movie 6.',
          posterUrl: 'https://via.placeholder.com/150'
        },
        {
            id: 6,
            title: 'Movie Title 6',
            description: 'This is a brief description of Movie 6.',
            posterUrl: 'https://via.placeholder.com/150'
          },
          {
              id: 6,
              title: 'Movie Title 6',
              description: 'This is a brief description of Movie 6.',
              posterUrl: 'https://via.placeholder.com/150'
            },
            {
                id: 6,
                title: 'Movie Title 6',
                description: 'This is a brief description of Movie 6.',
                posterUrl: 'https://via.placeholder.com/150'
              },
              {
                  id: 6,
                  title: 'Movie Title 6',
                  description: 'This is a brief description of Movie 6.',
                  posterUrl: 'https://via.placeholder.com/150'
                },
                {
                    id: 6,
                    title: 'Movie Title 6',
                    description: 'This is a brief description of Movie 6.',
                    posterUrl: 'https://via.placeholder.com/150'
                  }
      // Add more movies as needed
    ];
  const [keyword, setKeyword] = useState('');

  const handleSearch = (event) => {
    event.preventDefault();
    const options = {
      method: 'GET',
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJkMWNkNGQxYmE3YTU4YjE1MTM4MzVkOTM1YjI2YjJiZSIsInN1YiI6IjY1ZTc4OWY5NTFmOTlhMDE2MmZiYjc1NCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.vnaq5VRSoFW0czZA-0ldClHBIEhL-PWg1P7NXFi6VeA'
      }
    };

    fetch('https://api.themoviedb.org/3/search/movie?include_adult=false&language=en-US&page=1&query=' + keyword, options)
      .then(response => response.json())
      .then(response => console.log(response))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <div>
      <input
        type="text"
        placeholder="Search for Movie"
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        <MovieRecommender
            movies = {moviesTest}
        />
      </div>
    </div>
  );
}

export default SearchMovie;
